import React, { useState } from "react";
import AssignParcelModal from "./AssignParcelModal";

const Dboard = () => {
  const [selectedLocker, setSelectedLocker] = useState("");
  const [showModal, setShowModal] = useState(false);

  const handleLockerClick = (lockerId) => {
    setSelectedLocker(lockerId);
    setShowModal(true);
  };

  const communityName = localStorage.getItem("communityName") || "Main Building";

  const lockers = Array.from({ length: 18 }, (_, i) => {
    const row = String(Math.floor(i / 6) + 1).padStart(2, "0");
    const col = String((i % 6) + 1).padStart(2, "0");
    return `0${row}-${col}`;
  });

  return (
    <div className="p-6">
      <div className="flex justify-between items-center mb-4">
        <h2 className="text-2xl font-bold">Main Building</h2>
        <h3 className="text-xl font-semibold">
          Community ID: <span className="font-bold">COM001</span>
        </h3>
      </div>

      <div className="flex gap-4 mb-6">
        <button className="bg-gray-200 px-4 py-2 rounded">Staff</button>
        <button className="bg-gray-200 px-4 py-2 rounded">Residents</button>
        <button className="bg-gray-200 px-4 py-2 rounded">Parcel History</button>
      </div>

      <div className="mb-4">
        <p className="font-semibold text-lg">Locker - {communityName}</p>
        <p className="text-sm text-gray-600">Available ðŸ”“ | Occupied ðŸ”’</p>
      </div>

      <div className="grid grid-cols-6 gap-4">
        {lockers.map((locker) => (
          <div
            key={locker}
            className="bg-green-100 p-4 text-center rounded cursor-pointer hover:bg-green-200"
            onClick={() => handleLockerClick(locker)}
          >
            <img
              src="/lock-open.png"
              alt="Lock Icon"
              className="mx-auto w-5 h-5 mb-1"
            />
            {locker}
          </div>
        ))}
      </div>

      <AssignParcelModal
        show={showModal}
        lockerId={selectedLocker}
        onClose={() => setShowModal(false)}
      />
    </div>
  );
};

export default Dboard;
