// src/components/Dboard.jsx
import React, { useEffect, useState } from 'react';
import './Dboard.css';

const Dboard = () => {
  // Tabs: Locker Management, Staff, Parcel History
  const [activeTab, setActiveTab] = useState('Locker Management');

  // Fetch from localStorage
  const [boxCount, setBoxCount] = useState(1);
  const [lockersPerBox, setLockersPerBox] = useState(1);

  // For demo, admin/community from localStorage
  const communityName = localStorage.getItem('communityName') || 'Default Community';
  const adminName = localStorage.getItem('adminName') || 'Admin';

  useEffect(() => {
    const boxes = parseInt(localStorage.getItem('boxCount'), 10);
    const lockers = parseInt(localStorage.getItem('lockersPerBox'), 10);

    if (boxes && boxes > 0) setBoxCount(boxes);
    if (lockers && lockers > 0) setLockersPerBox(lockers);
  }, []);

  // Render lockers grouped by boxes
  const renderLockerBoxes = () => {
    const boxes = [];
    for (let i = 1; i <= boxCount; i++) {
      boxes.push(
        <div key={`box-${i}`} className="locker-box">
          <h4>{`Box ${i}`}</h4>
          <div className="locker-row">
            {Array.from({ length: lockersPerBox }).map((_, idx) => (
              <div key={`box${i}-locker${idx + 1}`} className="locker-tile available">
                <span role="img" aria-label="lock">ðŸ”“</span>
                <small>{`L${idx + 1}`}</small>
              </div>
            ))}
          </div>
        </div>
      );
    }
    return boxes;
  };

  // Tab content
  const renderTabContent = () => {
    switch (activeTab) {
      case 'Locker Management':
        return (
          <div className="locker-management">
            <h2>{communityName} - Locker Management</h2>
            {renderLockerBoxes()}
          </div>
        );
      case 'Staff':
        return (
          <div className="staff-section">
            <h2>Staff Management</h2>
            <p>Staff management content goes here.</p>
            {/* You can build staff list, add staff etc */}
          </div>
        );
      case 'Parcel History':
        return (
          <div className="parcel-history-section">
            <h2>Parcel History</h2>
            <p>Parcel history content goes here.</p>
            {/* You can fetch parcel history and display */}
          </div>
        );
      default:
        return null;
    }
  };

  return (
    <div className="dashboard-container">
      <div className="top-bar">
        <div>Community Manager Dashboard</div>
        <div>Community Name: <strong>{communityName}</strong></div>
        <div>Admin: <strong>{adminName}</strong></div>
      </div>

      <div className="tabs">
        {['Locker Management', 'Staff', 'Parcel History'].map((tab) => (
          <div
            key={tab}
            className={`tab ${activeTab === tab ? 'active' : ''}`}
            onClick={() => setActiveTab(tab)}
            style={{ cursor: 'pointer' }}
          >
            {tab}
          </div>
        ))}
      </div>

      <div className="dashboard-content">
        {renderTabContent()}
      </div>
    </div>
  );
};

export default Dboard;
