import React, { useEffect, useState } from 'react';
import './Dboard.css';

const Dboard = () => {
  const [boxCount, setBoxCount] = useState(3);
  const [lockersPerBox, setLockersPerBox] = useState(3);
  const [currentIndex, setCurrentIndex] = useState(0);
  const [showLocks, setShowLocks] = useState(false);

  useEffect(() => {
    const box = parseInt(localStorage.getItem('boxCount')) || 3;
    const lockers = parseInt(localStorage.getItem('lockersPerBox')) || 3;
    setBoxCount(box);
    setLockersPerBox(lockers);
  }, []);

  const handleNext = () => {
    if (currentIndex < boxCount - 1) {
      setCurrentIndex(currentIndex + 1);
      setShowLocks(false);
    }
  };

  const handlePrev = () => {
    if (currentIndex > 0) {
      setCurrentIndex(currentIndex - 1);
      setShowLocks(false);
    }
  };

  const handleLockerClick = () => {
    setShowLocks(true);
  };

  return (
    <div className="container">
      <div className="slider-wrapper">
        <div
          className="slider"
          style={{ transform: `translateX(-${currentIndex * 100}%)` }}
        >
          {Array.from({ length: boxCount }).map((_, i) => (
            <div key={i} className="locker-box" onClick={handleLockerClick}>
              Locker_{i + 1}
            </div>
          ))}
        </div>
      </div>

      {showLocks && (
        <div className="locks-box">
          {Array.from({ length: lockersPerBox }, (_, i) => (
            <div key={i} className="lock">Lock_{i + 1}</div>
          ))}
        </div>
      )}

      <div className="nav-buttons">
        <button onClick={handlePrev} disabled={currentIndex === 0}>← Prev</button>
        <button onClick={handleNext} disabled={currentIndex === boxCount - 1}>Next →</button>
      </div>
    </div>
  );
};

export default Dboard;
