      React.createElement(
        "button",
        { className: "bg-gray-200 px-4 py-2 rounded hover:bg-gray-300" },
        "Residents"
      ),
      React.createElement(
        "button",
        { className: "bg-gray-200 px-4 py-2 rounded hover:bg-gray-300" },
        "Parcel History"
      )
    ),
    React.createElement(
      "div",
      { className: "grid grid-cols-6 gap-4" },
      Array.from({ length: 12 }, (_, i) => {
        const lockerId = `Locker-${i + 1}`;
        return React.createElement(
          "div",
          {
            key: lockerId,
            className: "bg-green-100 p-4 text-center rounded cursor-pointer hover:bg-green-200",
            onClick: () => openModal(lockerId),
          },
          React.createElement("img", {
            src: "/lock-open.png",
            alt: "Lock Icon",
            className: "mx-auto w-5 h-5 mb-1",
          }),
          lockerId
        );
      })
    ),
    showModal &&
      React.createElement(AssignParcelModal, {
        lockerId: selectedLocker,
        onClose: () => setShowModal(false),
      })
  );
};

export default Dboard;
