import React, { useEffect, useState } from 'react';
import './Dboard.css';

const Dboard = () => {
  const [boxCount, setBoxCount] = useState(1);
  const [lockersPerBox, setLockersPerBox] = useState(1);
  const [currentLocker, setCurrentLocker] = useState(0);
  const [showLocks, setShowLocks] = useState(false);

  useEffect(() => {
    const box = parseInt(localStorage.getItem('boxCount')) || 1;
    const lockers = parseInt(localStorage.getItem('lockersPerBox')) || 1;
    setBoxCount(box);
    setLockersPerBox(lockers);
  }, []);

  const handleNext = () => {
    if (currentLocker < boxCount - 1) {
      setCurrentLocker(currentLocker + 1);
      setShowLocks(false); // reset view
    }
  };

  const handlePrev = () => {
    if (currentLocker > 0) {
      setCurrentLocker(currentLocker - 1);
      setShowLocks(false); // reset view
    }
  };

  const handleLockerClick = () => {
    setShowLocks(true);
  };

  return (
    <div className="container">
      {!showLocks ? (
        <div className="locker-box" onClick={handleLockerClick}>
          Locker_{currentLocker + 1}
        </div>
      ) : (
        <div className="locks-box">
          {Array.from({ length: lockersPerBox }, (_, i) => (
            <div key={i} className="lock">Lock_{i + 1}</div>
          ))}
        </div>
      )}

      <div className="nav-buttons">
        <button onClick={handlePrev} disabled={currentLocker === 0}>← Prev</button>
        <button onClick={handleNext} disabled={currentLocker === boxCount - 1}>Next →</button>
      </div>
    </div>
  );
};

export default Dboard;
