import React, { useState } from "react";

const AssignParcelModal = ({ lockerId, onClose }) => {
  const [formData, setFormData] = useState({
    recipientName: "",
    phone: "",
    productId: "",
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleAssign = () => {
    const { recipientName, phone, productId } = formData;
    if (!recipientName || !phone || !productId) {
      alert("Please fill in all fields.");
      return;
    }
    alert(`Assigned to ${recipientName} in ${lockerId}`);
    onClose();
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-30 flex justify-center items-center z-50">
      <div className="bg-white p-6 rounded-lg shadow-md w-[400px]">
        <h2 className="text-xl font-bold text-center mb-4">
          Assign New Parcel to {lockerId}
        </h2>

        <form className="space-y-4">
          <div>
            <label className="block mb-1">Recipient Name:</label>
            <input
              type="text"
              name="recipientName"
              className="w-full border rounded px-3 py-1"
              onChange={handleChange}
            />
          </div>

          <div>
            <label className="block mb-1">Phone Number:</label>
            <input
              type="text"
              name="phone"
              className="w-full border rounded px-3 py-1"
              onChange={handleChange}
            />
          </div>

          <div>
            <label className="block mb-1">Product ID / Description:</label>
            <input
              type="text"
              name="productId"
              className="w-full border rounded px-3 py-1"
              onChange={handleChange}
            />
          </div>

          <div className="flex justify-end pt-4 gap-2">
            <button
              type="button"
              onClick={handleAssign}
              className="bg-green-500 text-white px-4 py-2 rounded hover:bg-green-600"
            >
              Assign Parcel
            </button>
            <button
              type="button"
              onClick={onClose}
              className="bg-gray-300 px-4 py-2 rounded"
            >
              Cancel
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default AssignParcelModal;
