import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Dboard.css';

const Dboard = () => {
  const [locks, setLocks] = useState('');
  const [lockers, setLockers] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const savedLocks = localStorage.getItem('locks');
    const savedLockers = localStorage.getItem('lockers');

    if (savedLocks && savedLockers) {
      setLocks(savedLocks);
      setLockers(savedLockers);
    } else {
      navigate('/');
    }
  }, [navigate]);

  const renderLockers = () => {
    const numLockers = parseInt(lockers) || 10;
    const lockerElements = [];

    for (let i = 1; i <= numLockers; i++) {
      lockerElements.push(
        <div key={i} className="locker-box">
          L{i}
        </div>
      );
    }
    return lockerElements;
  };

  return (
    <div className="dashboard-container">
      <div className="nav-links">
        <a href="#">Dashboard</a> | <a href="#">Parcel History</a> | <a href="#">Logout</a>
      </div>

      <h2 className="dashboard-header">
        Hello, <strong>[Watchman Name]</strong>, Welcome to <strong>[Hostel Name]</strong>!
      </h2>

      <div className="locker-container">{renderLockers()}</div>

      <div className="button-container">
        <button>Retrieve Parcel</button>
        <button>View Parcel History</button>
        <button>Logout</button>
      </div>
    </div>
  );
};

export default Dboard;
