import React, { useState } from 'react';
import axios from 'axios';

function RegisterForm() {
  const [formData, setFormData] = useState({
    name: '',
    address: '',
    admin: '',
    mobile: '',
    lockerBoxes: '',
    lockersPerBox: ''
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const res = await axios.post('http://localhost:5000/api/register', formData);
      alert(res.data.message); // ✅ This shows a popup
    } catch (err) {
      alert('Registration failed');
      console.error(err);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input name="name" onChange={handleChange} placeholder="Name" />
      <input name="address" onChange={handleChange} placeholder="Address" />
      <input name="admin" onChange={handleChange} placeholder="Admin" />
      <input name="mobile" onChange={handleChange} placeholder="Mobile" />
      <input name="lockerBoxes" type="number" onChange={handleChange} placeholder="Locker Boxes" />
      <input name="lockersPerBox" type="number" onChange={handleChange} placeholder="Lockers Per Box" />
      <button type="submit">Register</button>
    </form>
  );
}

export default RegisterForm;
