import React, { useEffect, useState } from 'react';
import './Dboard.css';

const Dboard = () => {
  const [boxCount, setBoxCount] = useState(0);
  const [lockersPerBox, setLockersPerBox] = useState(0);
  const [selectedLockerIndex, setSelectedLockerIndex] = useState(null);
  const [locksInLocker, setLocksInLocker] = useState(4); // default or fetch from storage

  useEffect(() => {
    const box = parseInt(localStorage.getItem('boxCount')) || 1;
    const lockers = parseInt(localStorage.getItem('lockersPerBox')) || 1;
    const locks = parseInt(localStorage.getItem('locksInLocker')) || 4;
    setBoxCount(box);
    setLockersPerBox(lockers);
    setLocksInLocker(locks);
  }, []);

  const totalLockers = boxCount * lockersPerBox;

  const handleLockerClick = (index) => {
    setSelectedLockerIndex(index);
  };

  const handlePrev = () => {
    if (selectedLockerIndex > 0) setSelectedLockerIndex(selectedLockerIndex - 1);
  };

  const handleNext = () => {
    if (selectedLockerIndex < totalLockers - 1) setSelectedLockerIndex(selectedLockerIndex + 1);
  };

  const renderLocks = () => (
    <div className="single-locker-view">
      <h2>Locker_{selectedLockerIndex + 1}</h2>
      <div className="locks-container">
        {Array.from({ length: locksInLocker }, (_, i) => (
          <div key={i} className="lock-item">Lock {i + 1}</div>
        ))}
      </div>
      <div className="nav-buttons">
        <button onClick={handlePrev} disabled={selectedLockerIndex === 0}>⬅ Prev</button>
        <button onClick={handleNext} disabled={selectedLockerIndex === totalLockers - 1}>Next ➡</button>
      </div>
    </div>
  );

  const renderLockers = () =>
    Array.from({ length: totalLockers }, (_, i) => (
      <div key={i} className="locker" onClick={() => handleLockerClick(i)}>
        Locker_{i + 1}
      </div>
    ));

  return (
    <div className="dashboard-container">
      <h2>Locker Dashboard</h2>
      {selectedLockerIndex !== null ? renderLocks() : (
        <div className="lockers-grid">{renderLockers()}</div>
      )}
    </div>
  );
};

export default Dboard;
