import React, { useEffect, useState } from "react";
import AssignParcelModal from "./AssignParcelModal";
import './Dboard.css';

const Dboard = () => {
  const [communityName, setCommunityName] = useState("Default Community");
  const [selectedLocker, setSelectedLocker] = useState(null);
  const [showModal, setShowModal] = useState(false);

  useEffect(() => {
    const name = localStorage.getItem("communityName");
    if (name) setCommunityName(name);
  }, []);

  const openModal = (lockerId) => {
    setSelectedLocker(lockerId);
    setShowModal(true);
  };

  return (
    <div className="p-6">
      <h2 className="text-xl font-bold mb-4">
        Welcome to {communityName} Community Dashboard
      </h2>

      {/* Navigation Buttons */}
      <div className="flex justify-center gap-4 mb-6">
        <button className="bg-gray-200 px-4 py-2 rounded hover:bg-gray-300">
          Staff
        </button>
        <button className="bg-gray-200 px-4 py-2 rounded hover:bg-gray-300">
          Residents
        </button>
        <button className="bg-gray-200 px-4 py-2 rounded hover:bg-gray-300">
          Parcel History
        </button>
      </div>

      {/* Locker Grid */}
      <div className="grid grid-cols-6 gap-4">
        {Array.from({ length: 12 }, (_, i) => {
          const lockerId = `Locker-${i + 1}`;
          return (
            <div
              key={lockerId}
              className="bg-green-100 p-4 text-center rounded cursor-pointer hover:bg-green-200"
              onClick={() => openModal(lockerId)}
            >
              <img src="/lock-open.png" alt="Lock Icon" className="mx-auto w-5 h-5 mb-1" />
              {lockerId}
            </div>
          );
        })}
      </div>

      {/* Modal */}
      {showModal && (
        <AssignParcelModal
          lockerId={selectedLocker}
          onClose={() => setShowModal(false)}
        />
      )}
    </div>
  );
};

export default Dboard;
