import React, { useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom';
import './Dboard.css';

const Dboard = () => {
  const location = useLocation();
  const user = location.state?.user;

  const [lockers, setLockers] = useState([]);

  useEffect(() => {
    if (user?.numLockers) {
      const lockerArray = [];
      for (let i = 1; i <= user.numLockers; i++) {
        lockerArray.push({ lockerId: `L${i}` });
      }
      setLockers(lockerArray);
    }
  }, [user?.numLockers]);

  if (!user || !user.name || !user.address) {
    return <div>Loading user data...</div>;
  }

  return (
    <div className="dashboard-container">
      <div className="nav">
        <a href="#">Dashboard</a> | <a href="#">Parcel History</a> | <a href="#">Logout</a>
      </div>
      <h2>Hello, <strong>{user.name}</strong>, Welcome to <strong>{user.address}</strong>!</h2>

      <div className="lockers">
        {lockers.length > 0 ? (
          lockers.map(locker => (
            <div className="locker" key={locker.lockerId}>
              {locker.lockerId}
            </div>
          ))
        ) : (
          <p>No lockers assigned yet.</p>
        )}
      </div>

      <div className="buttons">
        <button>Retrieve Parcel</button>
        <button>View Parcel History</button>
        <button>Logout</button>
      </div>
    </div>
  );
};

export default Dboard;
